INSERT INTO `admin` VALUES ('1', '1', '1');
INSERT INTO `category` VALUES ('1', '系统分析师', '系统分析师对软件项目进行整体规划、需求分析、设计软件的核心架构、指导和领导项目开发小组进行软件开发和软件实现，并对整个项目进行全面的管理工作。\r\n', '1');
INSERT INTO `category` VALUES ('2', '系统架构设计师', '系统架构设计师是指进行数据库安装、配置、优化，根据需求进行数据的挖掘及展示；进行数据仓库方面的探索及开发的人员。架构师是由国外引进的一个概念，国外软件开发的几个职位是技术官、架构师、设计师、开发、测试，对应我们的公司应该是技术总监、架构师、系统分析员、程序员、测试人员。\r\n', '1');
INSERT INTO `category` VALUES ('3', '网络规划设计师', '网络规划设计师能够进行计算机网络领域的需求分析、规划设计、部署实施、评测、运行维护等工作。能指导制订用户的数据和网络战略规划，能指导网络工程师进行系统建设实施。\r\n', '1');
INSERT INTO `category` VALUES ('4', '系统规划与管理师', '系统规划与管理师主要在信息系统的开发工作中从事规划与管理工作。\r\n', '1');
INSERT INTO `category` VALUES ('5', '信息系统项目管理师', '信息系统项目管理师具备管理大型、复杂信息系统项目和多项目的经验和能力；能根据需求组织制订可行的项目管理计划；能够组织项目实施，对项目的人员、资金、设备、进度和质量等进行管理，并能根据实际情况及时做出调整，系统地监督项目实施过程的绩效，保证项目在一定的约束条件下达到既定的项目目标；能分析和评估项目管理计划和成果。 \r\n', '1');
INSERT INTO `category` VALUES ('6', '软件设计师', '软件设计师能根据软件开发项目管理和软件工程的要求，按照系统总体设计规格说明书进行软件设计，编写程序设计规格说明书等相应的文档；组织和指导程序员编写、调试程序，并对软件进行优化和集成测试，开发出符合系统总体设计要求的高质量软件；具有工程师的实际工作能力和业务水平。\r\n', '2');
INSERT INTO `category` VALUES ('7', '软件测评师', '软件测试工程师就是利用测试工具按照测试方案和流程对产品进行性能测试，甚至根据需要编写不同的测试工具、设计和维护测试系统，对测试方案可能出现的问题进行分析和评估，以确保软件产品的质量。 \r\n', '2');
INSERT INTO `category` VALUES ('8', '网络工程师', '网络工程师是通过学习和训练，掌握网络技术的理论知识和操作技能的网络技术人员。网络工程师能够从事计算机信息系统的设计、建设、运行和维护工作。\r\n', '2');
INSERT INTO `category` VALUES ('9', '信息系统监理师', '监理就是由建设方授权依照国家法律法规以及合同、行业标准、规范等对信息系统工程实施的监督和管理。在法律上是独立的第三方。与建设方签订委托合同。监理费用由建设方来承担。 \r\n', '2');
INSERT INTO `category` VALUES ('10', '信息安全工程师', '信息安全工程师是指遵照信息安全管理体系和标准工作，防范黑客入侵并进行分析和防范，通过运用各种安全产品和技术，设置防火墙、防病毒、IDS、PKI、攻防技术等。进行安全制度建设与安全技术规划、日常维护管理、信息安全检查与审计系统帐号管理与系统日志检查等的人员。\r\n', '2');
INSERT INTO `category` VALUES ('11', '程序员', '程序员是从事程序开发、维护的专业人员。\r\n', '3');
INSERT INTO `category` VALUES ('12', '网络管理员', '网络管理员是从事计算机网络运行、维护的人员。\r\n', '3');
INSERT INTO `category` VALUES ('13', '网页制作员', '网页制作员主要进行与网页制作相关的工作。\r\n', '3');
INSERT INTO `category` VALUES ('14', '信息处理技术员', '信息处理技术员具有计算机与信息处理的基础知识，能根据应用部门的要求，熟练使用计算机有效地、安全地进行信息处理操作，能对个人计算机系统进行日常维护。\r\n', '3');
INSERT INTO `category` VALUES ('15', '电子商务技术员', '电子商务技术员不仅应具有以上应知应会的知识和技能，还应具备参加电子商务系统规划、设计，特别是具体从事电子商务信息系统开发和运行维护工作的能力。\r\n', '3');
