
DROP TABLE IF EXISTS `admin`;
CREATE TABLE `admin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(255) DEFAULT NULL,
  `password` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `category`;
CREATE TABLE `category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) DEFAULT NULL COMMENT '名字',
  `intro` varchar(1024) DEFAULT NULL COMMENT '简介',
  `level` tinyint(4) DEFAULT NULL COMMENT '等级(1高级/2中级/3初级)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `grade`;
CREATE TABLE `grade` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `total` int(11) DEFAULT NULL COMMENT '答题数',
  `grade` int(11) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `category_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `question`;
CREATE TABLE `question` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(1024) DEFAULT NULL COMMENT '题目',
  `option1` varchar(255) DEFAULT NULL COMMENT 'A选项',
  `option2` varchar(255) DEFAULT NULL COMMENT 'B选项',
  `option3` varchar(255) DEFAULT NULL COMMENT 'C选项',
  `option4` varchar(255) DEFAULT NULL COMMENT 'D选项',
  `result` int(11) DEFAULT NULL COMMENT '答案',
  `score` int(11) DEFAULT NULL COMMENT '分值',
  `analy` varchar(1024) DEFAULT NULL COMMENT '解析',
  `category_id` int(11) DEFAULT NULL COMMENT '类目',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=175 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(20) DEFAULT NULL,
  `password` varchar(50) DEFAULT NULL,
  `email` varchar(20) DEFAULT NULL COMMENT '邮箱',
  `intro` varchar(512) DEFAULT NULL COMMENT '简介',
  `total` int(11) DEFAULT '0' COMMENT '积分',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

